sourceCode:
/* ==UserStyle==
@name           Zero-A
@namespace      https://github.com/Catgrills/Zero-A
@version        1.0.1
@description    An userstyle for Danbooru called Zero-A
@author         Catgrills/Katgrills
@preprocessor   uso
@var select theme 'Theme' {
	"Light": `
	:root {
        --body-bg: #EEE !important; 
		--card-bg: #F5F5F5 !important;
		--card-bg-2: #FFF !important;
		--border-color: #BEBEBE !important;
		--primary-color: #212121 !important;
		--secondary-color: #5E5E5E !important;
	}
	`,
	"Dark":`
	:root {
	    --body-bg: #254441 !important; 
		--card-bg: #25373D !important;
		--card-bg-2: #092327 !important;
		--border-color: #000 !important;
		--primary-color: #FFF !important;
		--secondary-color: #EBEBEB !important;
	}
	`
}
==/UserStyle== */
@-moz-document domain("danbooru.donmai.us") {
/*[[theme]]*/
/* GENERAL */

    /** DESCRIPTION : set the main font and change others elements **/
    * {
        font-family: "Roboto", "ぼくたちのゴシック２ボールド" !important;
    }

	*::-moz-focus-inner {
		border: 0 !important;
	}

	select {
		-webkit-appearance: none;
		-moz-appearance: none;
		text-indent: 0px;
		text-overflow: ellipsis;
		white-space: nowrap;
	}

    html {
        overflow-x: hidden !important;
    }

    body,
    #page {
        background: var(--body-bg) !important;
    }

    #upgrade-account-notice {
        display: none !important;
    }

    article img,
    .preview img {
        border: none !important;
    }

    #posts .hidden-posts-notice {
        visibility: hidden
    }

    footer#page-footer {
        display: none !important;
    }

    form.simple_form div.input input[type="text"], 
    form.simple_form div.input input[type="file"], 
    form.simple_form div.input input[type="password"], 
    form.simple_form div.input input[type="email"],
    .input.select.optional select,
    .search .input input,
    .search .input select,
    input[type="submit"] {
        background: var(--card-bg) !important;
        border: 1px solid var(--border-color) !important;
        border-radius: 2px !important;
        padding: 5px !important;
        margin-bottom: 5px !important;
        color: var(--primary-color) !important;
        font-stretch: condensed !important;
        font-size: 14pt !important;
        min-width: 100px !important;
    }

    .paginator li * {
        background: var(--card-bg) !important;
        border: none !important;
        border-radius: 2px !important;
        box-shadow: 0px 1px 2px rgba(0,0,0,.1) !important;
        color: var(--secondary-color);
        font-size: 14pt;
        font-stretch: condensed !important;
    }

	.paginator li a:hover {
		color: #2CC990 !important;        
	}

    .paginator .current-page span {
        color: #2CC990;
        font-size: 14pt !important;
        font-weight: normal !important;
    }

	.paginator .arrow {
        display: none !important;
    }

    /* HEADER */

    /** DESCRIPTION : change the elements of the header **/
    header#top h1 {

        /*** HEADER CONTAINER ***/
        background-color: #2CC990 !important;
        height: 45px !important;
        width: 100% !important;
        text-align: center !important;
        position: fixed !important;
        margin-left: 0px !important;
        top: -6px !important;
        line-height: 45px !important;
        z-index: 999 !important;
        transition: margin-left .5s;
    }

    header#top h1 a {
        font-size: 14pt !important;
        width: 100% !important;
        color: #FFF !important;
        visibility: hidden;
    }

    header#top h1 a::after {

        /*** HEADER TITLE ***/
        content: "DANBOORU 段ボール" !important;
        font-family: "Roboto", "KFHimaji" !important;
        font-stretch: condensed !important;
        width: 100% !important;
        position: absolute !important;
        left: 0px !important;
        right: 0px !important;
        margin: 10px auto !important;
        visibility: visible !important;
    }

    #nav {

        /*** HEADER MENU ***/
        background-color: #25373D !important;
        min-height: 1080px !important;
        width: 200px !important;
        padding-top: 30px !important;
        position: fixed !important;
        top: 0px !important;
        left: -200px;
        transition: all .3s ease-in-out !important;
        z-index: 999 !important;
    }

    header#top menu {
        background: transparent !important;
        margin-top: -10px !important;
    }

    header#top menu li {
        width: 100% !important;
        overflow: hidden;
        white-space: nowrap !important;
        display: block !important;
        padding: 0px 0px 9px 0px !important;
    }

    header#top menu li a {
        color: #FFF !important;
        font-size: 14pt !important;
        font-stretch: condensed !important;
        font-weight: normal !important;
        transition: all .3s ease-in-out !important;
    }

    header#top menu li a:hover {
        color: #BEBEBE !important;
        transition: all .3s ease-in-out !important;
    }

    header#top menu.main li.current a {
        background: transparent !important;
        color: #BEBEBE !important;
    }

    #quick_search_body_matches,
    #quick_search_name_matches,
    #quick_search_name,
    #quick_search_title {
        background: transparent !important;
        border: none !important;
        color: #FFF !important;
        font-size: 14pt !important;
        font-stretch: condensed !important;
        font-weight: normal !important;
        padding: 0px 8px !important;
        margin-left: 0px !important;
    }

    #trigger-btn {
        background: no-repeat url("https://i.imgur.com/3wq5etY.png") center !important;
        background-color: transparent;
        background-size: 22px auto !important;
        border: none !important;
        color: transparent !important;
        position: absolute !important;
        top: 11px !important;
        right: -48px !important;
    }

    .ui-autocomplete {
        font-size: 15pt !important;
        font-stretch: condensed !important;
    }

	.ui-state-focus {
        margin: 0px !important;  
	}

    /* ACCOUNT */

    /** DESCRIPTION : change the elements of the account page **/
    #c-users #a-show {

        /*** ACCOUNT MAIN CONTAINER ***/
        background: var(--card-bg-2) !important;
        border: 1px solid var(--border-color) !important;
        width: 95% !important;
        padding: 12px 20px !important;
        position: absolute !important;
        left: 0px !important;
        right: 0px !important;
        margin: 65px auto !important;
    }

    #c-users #a-show .user-member {
        color: #2C82C9 !important;
        font-stretch: condensed !important;
        text-transform: capitalize !important;
        margin-bottom: -40px !important;
    }

    #c-users #a-show h1,
    #c-users #a-show h2,
    #c-users #a-show h3,
    #c-users #a-show h4,
    #c-users #a-show strong {
        color: var(--primary-color);
        font-stretch: condensed !important;
        text-transform: uppercase !important;
        margin-bottom: 10px !important;
    }

    #c-users > #a-show > div:nth-of-type(1) {
        border-top: 1px solid var(--border-color) !important;
        padding: 20px 30px 0px 30px !important;
        margin-top: 15px !important;
        margin-left: -20px !important;
        margin-right: -20px !important;
    }

	#c-users #a-show table.user-statistics * {
        color: var(--secondary-color);
		font-size: 15pt;        
        font-stretch: condensed;
	}

    #c-users #a-show table.user-statistics th {
        color: var(--primary-color) !important;
        font-stretch: condensed !important;
        font-weight: normal !important;
        text-align: left !important;
        text-transform: Capitalize !important;
    }

    #c-users #a-show .box.user-favorites {
        border-top: 1px solid var(--border-color) !important;
        padding: 20px 30px 0px 30px !important;
        margin-top: 30px !important;
        margin-left: -20px !important;
        margin-right: -20px !important;
    }

    #c-users #a-show .box.user-favorites a {
        color: #5C9F97 !important;
    }

    #options-box {
        display: none !important;
    }

    /* ARTIST/TAG/POOL/WIKI/NOTES/SEARCHES/CHANGES/UPLOADS/APPEALS/ETC */

    /** DESCRIPTION : change the elements of the artist/tag/poll/notes/searches/changes/etc page **/
    #c-artists #a-index,
    #c-tags #a-index,
    #c-pools #a-index,
    #c-notes #a-index,
    #c-wiki-pages #a-index,
    #c-wiki-page-versions #a-index,
    #c-uploads #a-index,
    #c-post-appeals #a-index,
    #c-post-flags #a-index,
    #c-post-replacements #a-index,
    #c-notes-versions #a-index,
    #c-artist-versions #a-index,
    #c-artist-commentary-versions #a-index,
    #c-artist-commentaries #a-index,
    #c-post-versions #a-index,
    #c-explore-posts #a-searches,
    #c-tag-aliases #a-index,
    #c-tag-implications #a-index,
    #c-tag-subscriptions #a-index,
    #c-forum-topics #a-index,
    #c-forum-posts #a-index,
    #c-static #a-keyboard-shortcuts,
    #c-static #a-bookmarklet,
    #c-users #a-index,
    #c-users #a-edit,
    #c-user-feedbacks #a-index,
    #c-bans #a-index,
    #c-mod-actions #a-index,
    #c-delayed-jobs #a-index,
    #c-burs #a-index,
    #c-janitor-trials #a-index,
    #a-search,
    #a-new,
    #a-request-new,
    .bulk-update-requests {

        /*** ARTIST/TAG/POOL LIST MAIN CONTAINER ***/
        background: var(--card-bg-2) !important;
        border: 1px solid var(--border-color) !important;
        width: 90% !important;
        padding: 12px 20px !important;
        position: absolute !important;
        left: 0px !important;
        right: 0px !important;
        margin: 65px auto !important;
    }

    #c-artists #a-index .simple_form.inline-form,
    #c-tags #a-index .simple_form.inline-form,
    #c-post-appeals .simple_form.inline-form,
    #c-post-flags #a-index .simple_form.inline-form,
    #c-post-replacements #a-index .simple_form.inline-form,
    #c-tag-aliases #a-index .simple_form.inline-form,
    #c-tag-implications #a-index .simple_form.inline-form {

        /*** ARTIST/TAG/POOL LIST SEARCH ***/
        margin-top: 5px !important;
    }

    #c-pools #a-index .search {
        margin-top: 5px !important;
        margin-bottom: 10px !important;
    }

    #c-pools #a-gallery .search {
        margin-top: 75px !important;
        margin-bottom: 10px !important;
    }

    #c-pools #a-index .search th,
    #c-pools #a-gallery .search th {
        text-align: left !important;
    }

    #c-pools #a-index .search label,
    #c-pools #a-gallery .search label {
        font-stretch: condensed !important;
    }

    #c-pools #a-index .search *,
    #c-pools #a-gallery .search * {
        color: var(--primary-color);
        font-size: 14pt !important;
        font-stretch: condensed !important;
        font-weight: normal !important;
    }

    #c-wiki-pages #a-index #content h1,
    #c-wiki-page-versions #a-index #content h1 {
        display: none !important;
    }

    #c-wiki-pages #a-index #content,
    #c-wiki-page-versions #a-index #content {
        margin-top: 10px !important;
    }

    #c-wiki-pages #a-index #sidebar h1,
    #c-wiki-page-versions #a-index #sidebar h1 {
        font-stretch: condensed !important;
        text-transform: uppercase !important;
        margin-bottom: 10px !important;
        margin-top: 5px !important;
    }

    #c-notes #a-index h1,
    #c-notes-versions #a-index h1,
    #c-artist-versions #a-index h1,
    #c-artist-commentary-versions #a-index h1,
    #c-artist-commentaries #a-index h1,
    #c-post-versions #a-index h1,
    #c-post-appeals #a-index h1,
    #c-post-flags #a-index h1,
    #c-post-replacements #a-index h1,
    #c-explore-posts #a-searches h1,
    #c-forum-topics #a-index h1,
    #c-forum-posts #a-index h1,
    #c-static #a-keyboard-shortcuts > h1,
    #c-static #a-bookmarklet h1,
    #c-tag-subscriptions #a-index h1,
    #c-users #a-index h1,
    #c-users #a-edit h1,
    #c-user-feedbacks #a-index h1,
    #c-bans #a-index h1,
    #c-mod-actions #a-index h1,
    #c-delayed-jobs #a-index h1,
    #c-burs #a-index h1,
    #c-janitor-trials #a-index h1,
    #a-search h1,
    #a-request-new h1,
    #a-new h1,
    .bulk-update-requests h1 {
        color: var(--primary-color);
        font-size: 17pt !important;
        font-stretch: condensed !important;
        text-transform: uppercase !important;
        margin-bottom: 10px !important;
    }

    #a-index > p:nth-of-type(1) {
        color: var(--secondary-color);        
        font-size: 15pt !important;
        font-stretch: condensed !important;
    }

    #c-artists #a-index .striped,
    #c-tags #a-index .striped,
    #c-pools #a-index .striped,
    #c-notes #a-index .striped,
    #c-wiki-pages #a-index .striped,
    #c-wiki-page-versions #a-index .striped,
    #c-uploads #a-index .striped,
    #c-post-appeals #a-index .striped,
    #c-post-flags #a-index .striped,
    #c-post-replacements #a-index .striped,
    #c-notes-versions #a-index .striped,
    #c-artist-versions #a-index .striped,
    #c-artist-commentary-versions #a-index .striped,
    #c-artist-commentaries #a-index .striped,
    #c-post-versions #a-index .striped,
    #c-explore-posts #a-searches .striped,
    #c-tag-aliases #a-index .striped,
    #c-tag-sibscriptions #a-index .striped,
    #c-forum-topics #a-index .striped,
    #c-forum-posts #a-index .striped,
    #c-users #a-index .striped,
    #c-user-feedbacks #a-index .striped,
    #c-bans #a-index .striped,
    #c-mod-actions #a-index .striped,
    #c-delayed-jobs #a-index .striped,
    #c-burs #a-index .striped,
    #c-janitor-trials #a-index .striped {

        /*** ARTIST/TAG/POOL LIST TABLE ***/
        background: var(--card-bg) !important;
        border: 1px solid var(--border-color) !important;
        width: 100% !important;
        margin-bottom: -1px !important;
    }

    table.striped tr.even,
    table.striped tbody tr {
        background: var(--card-bg) !important;
        border: 1px solid var(--border-color) !important;      
    }

    #c-uploads #a-index .striped {
        margin-top: 10px !important;
    }

    #c-forum-posts #a-index .striped {
        margin-top: 10px !important;
        margin-bottom: -11px !important;
    }

    #c-artists #a-index thead,
    #c-tags #a-index thead,
    #c-pools #a-index thead,
    #c-notes #a-index thead,
    #c-wiki-pages #a-index thead,
    #c-wiki-page-versions #a-index thead,
    #c-uploads #a-index thead,
    #c-post-appeals #a-index thead,
    #c-post-flags #a-index thead,
    #c-post-replacements #a-index thead,
    #c-notes-versions #a-index thead,
    #c-artist-versions #a-index thead,
    #c-artist-commentary-versions #a-index thead,
    #c-artist-commentaries #a-index thead,
    #c-post-versions #a-index thead,
    #c-tag-aliases #a-index thead,
    #c-tag-implications #a-index thead,
    #c-explore-posts #a-searches thead,
    #c-forum-topics #a-index thead,
    #c-forum-posts #a-index thead,
    #c-tag-subscriptions #a-index thead,
    #c-users #a-index thead,
    #c-user-feedbacks #a-index thead,
    #c-bans #a-index thead,
    #c-mod-actions #a-index thead,
    #c-delayed-jobs #a-index thead,
    #c-burs #a-index thead,
    #c-janitor-trials #a-index thead {
        display: none;
    }

    .striped td {
        background: transparent !important;
        color: var(--primary-color);
        font-size: 15pt !important;
        font-stretch: condensed !important;
    }

    #c-artists #a-index hr,
    #c-tags #a-index hr,
    #c-pools #a-index hr,
    #c-notes #a-index hr,
    #c-wiki-pages #a-index hr,
    #c-wiki-page-versions #a-index hr,
    #c-uploads #a-index hr,
    #c-post-appeals #a-index hr,
    #c-post-flags #a-index hr,
    #c-post-replacements #a-index hr,
    #c-post-notes-versions #a-index hr,
    #c-artist-versions #a-index hr,
    #c-artist-commentary-versions #a-index hr,
    #c-artist-commentaries #a-index hr,
    #c-artist-commentaries #a-index hr,
    #c-versions #a-index hr,
    #c-tag-aliases #a-index hr,
    #c-tag-implications #a-index hr,
    #c-forum-topics #a-index hr,
    #c-forum-posts #a-index hr,
    #c-users #a-index hr,
    #c-user-feedbacks #a-index hr,
    #c-bans #a-index hr,
    #c-mod-actions #a-index hr,
    #c-delayed-jobs #a-index hr,
    #c-burs #a-index hr,
    #c-janitor-trials #a-index hr {

        /*** ARTIST LIST PAGINATION/HR ***/
        visibility: hidden;
    }

    #c-artists #a-show {

        /*** ARTIST MAIN CONTAINER ***/
        background: var(--card-bg-2) !important;
        border: 1px solid var(--border-color) !important;
        width: 90% !important;
        padding: 12px 20px !important;
        position: absolute !important;
        left: 0px !important;
        right: 0px !important;
        margin: 65px auto !important;
    }

    #c-artists #a-show h1,
    #c-artists #a-show h2,
    #c-artists #a-show h3,
    #c-artists #a-show h4,
    #c-artists #a-show strong {
        color: var(--primary-color);
        font-stretch: condensed !important;
        text-transform: uppercase !important;
        margin-bottom: 10px !important;
    }

    #c-artists #a-show ul li,
    #c-artists #a-show ul li * {
        color: var(--primary-color);
        font-size: 16pt !important;
        font-stretch: condensed !important;
    }

    #c-artists #a-show h1 a {

        text-transform: capitalize !important;
    }

    #c-artists #a-show .prose {
        border-top: 1px solid var(--border-color) !important;
        padding: 20px 30px 0px 20px !important;
        margin-top: 15px !important;
        margin-left: -20px !important;
        margin-right: -20px !important;
    }

    #c-artists #a-show .recent-posts {
        border-top: 1px solid var(--border-color) !important;
        padding: 20px 30px 0px 30px !important;
        margin-top: 30px !important;
        margin-left: -20px !important;
        margin-right: -20px !important;
    }

    /* SITE MAP */

    /** DESCRIPTION : change the elements of the site map page **/
    #c-static #a-site-map {

        /*** SITE MAP MAIN CONTAINER ***/
        background: var(--card-bg-2) !important;
        border: 1px solid var(--border-color) !important;
        width: 90% !important;
        padding: 12px 20px !important;
        position: absolute !important;
        left: 0px !important;
        right: 0px !important;
        margin: 65px auto !important;
    }

    #c-static #a-site-map h1,
    #c-static #a-site-map h2,
    #c-static #a-site-map h3,
    #c-static #a-site-map h4,
    #c-static #a-site-map strong {
        color: var(--primary-color) !important;
        font-size: 16pt !important;
        font-stretch: condensed !important;
        text-transform: uppercase !important;
        margin-bottom: 10px !important;
    }

    #a-site-map li a {
        font-size: 15pt !important;
        font-stretch: condensed !important;
        line-height: 20pt !important;
    }

    /* WIKI */

    /** DESCRIPTION : change the elements of the wiki page **/
    #c-wiki-pages #a-show {

        /*** WIKI MAIN CONTAINER ***/
        background: var(--card-bg-2) !important;
        border: 1px solid var(--border-color) !important;
        width: 90% !important;
        padding: 12px 20px !important;
        position: absolute !important;
        left: 0px !important;
        right: 0px !important;
        margin: 65px auto !important;
    }

    #c-wiki-pages #a-show h1,
    #c-wiki-pages #a-show h2,
    #c-wiki-pages #a-show h3,
    #c-wiki-pages #a-show h4,
    #c-wiki-pages #a-show strong {
        color: var(--primary-color);
        font-size: 15pt !important;
        font-stretch: condensed !important;
        font-weight: normal;
        text-transform: uppercase !important;
        margin-bottom: 20px !important;
    }

    #c-wiki-pages #content * {
        color: var(--primary-color);
        font-size: 15pt !important;
        font-stretch: condensed !important;
    }

    .wiki-other-name {
     	background: #2CC990 !important;
        border-radius: 2px !important;
        box-shadow: 0px 1px 2px rgba(0,0,0,.1) !important;
        padding-right: 10px !important;
        padding-left: 10px !important;
    }

    #wiki-page-body {
        margin-top: -9px !important;
        margin-left: -20px !important;
    }

    #wiki-page-body .expandable {
        border: 1px solid #212121 !important;
    }

    #c-wiki-pages #a-show #wiki-page-title {
        margin-top: 5px !important;
        margin-left: -25px !important;
    }

    #wiki-page-posts {
        margin-left: -14px !important;
    }

    #wiki-page-posts .wiki-other-name {
        display: none !important;
    }

    #c-posts #a-index #excerpt .wiki-other-name {
        background-color: var(--card-bg-2) !important;
        border-radius: 2px !important;
        box-shadow: 0px 1px 2px rgba(0,0,0,.1) !important;
        padding: 5px 7px !important;
        margin-right: 5px !important;
    }

	#c-wiki-pages .post-preview {
        border: 2px solid var(--border-color);
        height: 100% !important;
        float: left !important;
        margin: 10px 0px 10px 10px !important;
	}

    #c-wiki-pages #a-show #sidebar {

        /*** WIKI SIDEBAR ***/
        font-size: 15pt !important;
        font-stretch: condensed !important;
        margin-top: 5px !important;
        margin-left: 0px !important;
