    }

    #c-forum-topics span.new {
        color: #71BA51 !important; 
        font-weight: bold !important;
    }

    .list-of-forum-posts {

        /*** FORUM TOPIC POSTS ***/
        margin-top: 125px !important;
    }

    #c-forum-topics #a-show .forum-post {
        background: var(--card-bg-2) !important;
        border: 1px solid var(--border-color) !important;
        box-shadow: none !important;
        padding: 20px !important;
        margin-bottom: 10px !important;
    }

    #c-forum-topics #a-show .forum-post .author .user-post-uploader,
	.author h4 a {
        color: #2C82C9 !important;
        font-size: 17pt !important;
        font-stretch: condensed !important;
        font-weight: normal !important;
    }

    .list-of-forum-posts article .author time {
        color: var(--secondary-color) !important;
        font-size: 15pt !important;
        font-stretch: condensed !important;
        font-style: normal !important;
        line-height: 24pt !important;
    }

    .list-of-forum-posts .content * {
        color: var(--primary-color);
        font-size: 15pt;
        font-stretch: condensed;
    }

    blockquote {
    	background: var(--card-bg);
        border-color: var(--border-color);
    }

    .forum-post .prose .expandable {
        background: var(--card-bg);
        border: 1px solid var(--border-color) !important;
    }

    .prose code {
        background: var(--card-bg) !important;
        color: var(--primary-color) !important;
    }

    .expandable-header {
        padding-left: 14px !important;
    }

    .expandable-header * {
    	font-weight: normal !important;    
    }

    .expandable-content pre {
        border: 1px solid var(--border-color)!important;
        padding: 10px 15px !important;
        margin-top: 0px !important;
    }

    .prose .expandable-content {
        border-top: 1px solid var(--border-color)!important;
    }

    #c-forum-topics #a-show .forum-post .info {
        font-style: normal !important;
        font-size: 15pt !important;
    }

    #c-forum-topics #a-show .list-of-forum-posts .forum-post .content .prose pre {
        background: var(--card-bg) !important;
        border: 1px solid var(--border-color) !important;
        padding: 10px 15px !important;
    }

    input[value ='Show'],
    input[value ='Hide'] {
        background: var(--card-bg) !important;
        border: 1px solid var(--border-color) !important;
        color: var(--secondary-color) !important;
        font-stretch: condensed !important;
        font-weight: bold !important;
        padding: 5px 15px 6px 15px !important;
        text-transform: uppercase !important;
        margin-right: 5px !important;
    }

    #c-forum-topics > #a-show > p {

        /*** FORUM TOPIC REPLY BUTTON ***/
        text-align: center !important;
        margin-top: 40px !important;
        margin-bottom: 50px !important;
    }

    #new-response-link {
        background: no-repeat url("https://designmodo.com/wp-content/themes/designmodov2.1/flat-ui/images/join-conversation.png") center !important;
        background-size: 20px auto !important;
        color: transparent;
        width: 100% !important;
    }

    #topic-response #new_forum_post {
        text-align: center !important;
        margin-bottom: 30px !important;
    }

    #c-forum-topics #a-show .paginator {

        /*** FORUM PAGINATION ***/
        margin-top: -30px !important;
        margin-bottom: 30px !important;
    }

    /* COMMENTS */

    /** DESCRIPTIN : change the elements of the comments page **/
    #c-comments {
        width: 90% !important;
        position: absolute !important;
        left: 0px !important;
        right: 0px !important;
        margin: 80px auto !important;
    }

    #c-comments #a-index h1,
    #c-comments #a-index h2 {

        /*** COMMENTS TITLES ***/
        display: none !important;
    }

    #c-comments #a-index .post {

        /*** COMMENTS POSTS ***/
        background: var(--card-bg-2) !important;
        border: 1px solid var(--border-color) !important;
        padding: 20px 20px 30px 20px !important;
        margin-bottom: 10px !important;
    }

    #c-comments #a-index .preview {

        /*** COMMENTS THUMBNAILS ***/
        border: 2px solid var(--border-color) !important;
        height: 100% !important;
        width: auto !important;
    }

    #c-comments #a-index .comments-for-post .header * {
        color: var(--primary-color);
        font-size: 15pt !important;
        font-stretch: condensed !important;
        font-weight: normal !important;
    }

    #c-comments #a-index .list-of-comments * {
        color: var(--primary-color);
        font-size: 15pt !important;
        font-stretch: condensed !important;
    }

    #c-comments .comment {
        background: var(--card-bg) !important;
        border: 1px solid var(--border-color) !important;
        padding: 20px !important;
        margin-bottom: 20px !important;
    }

}

